import { LOGIN, REGISTER } from '../types';

const initialState = {
    user: {},
    isAuth: false,
};

export const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            return {
                ...state,
                user: action.payload,
            };

        case REGISTER:
            return {
                ...state,
                user: action.payload,
            };

        case CHECK_AUTH:
            return {
                ...state,
                user: action.payload,
            };

        default:
            state;
    }
};
