import React from 'react';
import { View, StyleSheet, Text, Image, Dimensions } from 'react-native';

import { THEME } from '../theme';
import { AppTextBold } from './ui/AppTextBold';

export const AppPlayTable = ({ player, count, style }) => {
    return (
        <View style={{ ...styles.block, ...style }}>
            <AppTextBold style={styles.text}>
                {player} - {count}
            </AppTextBold>
            <View style={styles.cards}>
                <Image style={styles.image} source={require('../../assets/cards/Diamonds_2.png')} />
                <Image style={styles.image} source={require('../../assets/cards/Diamonds_2.png')} />
                <Image style={styles.image} source={require('../../assets/cards/Diamonds_2.png')} />
                <Image style={styles.image} source={require('../../assets/cards/Diamonds_2.png')} />
                <Image style={styles.image} source={require('../../assets/cards/Diamonds_2.png')} />
            </View>
        </View>
    );
};

const width = Dimensions.get('window').width - 10;

const styles = StyleSheet.create({
    block: {
        width: width,
        backgroundColor: THEME.BORDER_COLOR,
        marginLeft: 'auto',
        marginRight: 'auto',
        paddingHorizontal: 4,
        paddingVertical: 5,
        borderRadius: 10,
    },
    text: {
        textAlign: 'center',
        fontSize: 18,
        color: '#fff',
    },
    cards: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginTop: 5,
    },
    image: {
        width: width / 5 - 5,
        height: 100,
        resizeMode: 'stretch',
    },
});
