import React from 'react';
import { View, StyleSheet, Dimensions, TouchableOpacity, Platform } from 'react-native';

import { THEME } from '../../theme';
import { AppTextBold } from './AppTextBold';

export const AppButton = ({ title, style, onPress, styleButton }) => {
    return (
        <TouchableOpacity activeOpacity={0.4} onPress={onPress}>
            <View style={{ ...styles.button, ...style, ...styleButton }}>
                <AppTextBold style={{ ...styles.text, ...style }}>{title}</AppTextBold>
            </View>
        </TouchableOpacity>
    );
};

const styles = StyleSheet.create({
    button: {
        width: Dimensions.get('window').width - 60,
        borderRadius: 10,
        backgroundColor: THEME.MAIN_COLOR,
        padding: 9,
        marginBottom: 15,
    },
    text: {
        fontSize: 20,
        textAlign: 'center',
        color: '#fff',
    },
});
