import React from 'react';
import { View, StyleSheet, Text, Modal, Button } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

import { AppTextBold } from './ui/AppTextBold';
import { AppTextInput } from './ui/AppTextInput';
import { THEME } from '../theme';
import { AppButton } from './ui/AppButton';

export const AppModal = ({ children, title, modalVisible, setModalVisible }) => {
    return (
        <Modal animationType="slide" visible={modalVisible}>
            <View style={styles.goBack}>
                <Ionicons.Button
                    name="chevron-back-sharp"
                    backgroundColor="#FFF"
                    size={34}
                    color={THEME.MAIN_COLOR}
                    iconStyle={{ marginRight: 0 }}
                    fadeDuration={0}
                    onPress={() => setModalVisible(!modalVisible)}>
                    Назад
                </Ionicons.Button>
            </View>
            <AppTextBold style={styles.title}>Вход</AppTextBold>
            <View style={styles.form}>
                <AppTextInput title="Логин" style={{ marginBottom: 20 }} />
                <AppTextInput title="Пароль" />
                <AppButton title="Войти" />
            </View>
        </Modal>
    );
};

const styles = StyleSheet.create({
    goBack: {
        paddingTop: 15,
        paddingLeft: 2,
        justifyContent: 'flex-start',
        alignItems: 'flex-start',
    },
    title: {
        fontSize: 20,
        fontFamily: 'roboto-bold',
        textAlign: 'center',
        color: THEME.MAIN_COLOR,
    },
    form: {
        alignItems: 'center',
    },
});
