import React from 'react';
import { View, StyleSheet, Modal } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

import { AppTextBold } from './ui/AppTextBold';
import { AppTextInput } from './ui/AppTextInput';
import { THEME } from '../theme';
import { AppButton } from './ui/AppButton';

export const AppModalRegister = ({ children, title, modalVisible, setModalVisible }) => {
    return (
        <Modal animationType="slide" visible={modalVisible} presentationStyle="fullScreen">
            <View style={styles.goBack}>
                <Ionicons.Button
                    name="chevron-back-sharp"
                    backgroundColor="#FFF"
                    size={34}
                    color={THEME.MAIN_COLOR}
                    iconStyle={{ marginRight: 0, hover: false }}
                    fadeDuration={0}
                    onPress={() => setModalVisible(!modalVisible)}>
                    Назад
                </Ionicons.Button>
            </View>
            <AppTextBold style={styles.title}>Регистрация</AppTextBold>
            <View style={styles.form}>
                <AppTextInput title="Логин" style={{ marginBottom: 15 }} />
                <AppTextInput title="Пароль" style={{ marginBottom: 15 }} password={true} />
                <AppTextInput
                    title="Подтверждение пароля"
                    style={{ marginBottom: 15 }}
                    password={true}
                />
                <AppTextInput title="Емаил" style={{ marginBottom: 30 }} />
                <AppButton title="Зарегистрироваться" />
            </View>
        </Modal>
    );
};

const styles = StyleSheet.create({
    goBack: {
        paddingTop: 15,
        paddingLeft: 2,
        justifyContent: 'flex-start',
        alignItems: 'flex-start',
    },
    title: {
        fontSize: 20,
        fontFamily: 'roboto-bold',
        textAlign: 'center',
        marginBottom: 20,
        color: THEME.MAIN_COLOR,
    },
    form: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
});
