import React from 'react';
import { View, StyleSheet, Dimensions, TouchableOpacity, Platform } from 'react-native';

import { THEME } from '../../theme';
import { AppTextBold } from './AppTextBold';

export const AppButton = ({ title, style, onOpen }) => {
    return (
        <TouchableOpacity activeOpacity={0.6} onPress={onOpen}>
            <View style={{ ...styles.button, ...style }}>
                <AppTextBold style={styles.text}>{title}</AppTextBold>
            </View>
        </TouchableOpacity>
    );
};

const styles = StyleSheet.create({
    button: {
        width: Dimensions.get('window').width - 60,
        borderRadius: 10,
        backgroundColor: Platform.OS === 'android' ? THEME.MAIN_COLOR : '#fff',
        borderWidth: Platform.OS === 'android' ? 0 : 2,
        borderColor: Platform.OS === 'android' ? null : THEME.MAIN_COLOR,
        padding: 9,
        marginBottom: 15,
        paddingLeft: 'auto',
        paddingRight: 'auto',
    },
    text: {
        fontSize: 20,
        textAlign: 'center',
        color: Platform.OS === 'android' ? '#fff' : THEME.MAIN_COLOR,
    },
});
