import React, { useState } from 'react';
import {
    View,
    StyleSheet,
    Dimensions,
    Platform,
    Keyboard,
    KeyboardAvoidingView,
    TouchableWithoutFeedback,
} from 'react-native';

import { AppPlayTable } from '../components/AppPlayTable';
import { AppButton } from '../components/ui/AppButton';
import { AppTextInput } from '../components/ui/AppTextInput';

import { THEME } from '../theme';

export const PlayScreen = () => {
    return (
        <KeyboardAvoidingView
            behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
            style={styles.container}>
            <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
                <View style={styles.screen}>
                    <View style={styles.bet}>
                        <AppTextInput styleInput={styles.inputBet} title="Ставка" />
                        <AppButton
                            title="Сделать ставку"
                            style={styles.buttonText}
                            styleButton={styles.button}
                        />
                    </View>
                    <View style={styles.bottom}>
                        <AppPlayTable player="Диллер" count="10" />
                        <AppPlayTable player="6y6exa" count="10" style={{ marginTop: 20 }} />
                        <View style={styles.buttonsPlay}>
                            <AppButton
                                title="Взять еще"
                                style={styles.buttonText}
                                styleButton={styles.button}
                            />
                            <AppButton
                                title="Стоп"
                                style={styles.buttonText}
                                styleButton={styles.button}
                            />
                        </View>
                    </View>
                </View>
            </TouchableWithoutFeedback>
        </KeyboardAvoidingView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    screen: {
        flex: 1,
        backgroundColor: THEME.MAIN_COLOR,
        justifyContent: 'center',
    },
    bottom: {
        alignItems: 'center',
    },
    bet: {
        marginTop: Platform.OS === 'android' ? 0 : 20,
        width: Dimensions.get('window').width - 10,
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginLeft: 'auto',
        marginRight: 'auto',
        marginBottom: Platform.OS === 'android' ? 40 : 20,
    },
    inputBet: {
        width: Dimensions.get('window').width / 2 - 10,
        fontSize: 20,
        paddingVertical: 8,
    },
    button: {
        width: Dimensions.get('window').width / 2 - 10,
        backgroundColor: THEME.BORDER_COLOR,
        paddingVertical: 8,
    },
    buttonText: {
        color: '#fff',
        fontSize: 20,
    },
    buttonsPlay: {
        marginTop: 50,
        width: Dimensions.get('window').width - 10,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
    },
});
