import React from 'react';
import { View, StyleSheet, Text, Image } from 'react-native';

import { AppTextBold } from '../components/AppTextBold';
import { AppButton } from '../components/AppButton';
import { THEME } from '../theme';

export const MainScreen = () => {
    return (
        <View style={styles.center}>
            <Image style={styles.image} source={require('../../assets/images/main_logo.png')} />
            <AppTextBold style={{ fontSize: 22, marginTop: 10, color: THEME.MAIN_COLOR }}>
                Играй и выигрывай с нами!
            </AppTextBold>
            <View style={styles.buttons}>
                <AppButton title="Войти" />
                <AppButton title="Зарегистрироваться" />
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    center: {
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'center',
        padding: 50,
    },
    image: {
        marginTop: 100,
        paddingLeft: 'auto',
        paddingRight: 'auto',
        width: 200,
        height: 200,
    },
});
