import React, { useState } from 'react';
import { View, StyleSheet, Text, Image } from 'react-native';

import { AppTextBold } from '../components/ui/AppTextBold';
import { AppButton } from '../components/ui/AppButton';
import { THEME } from '../theme';
import { AppModalLogin } from '../components/AppModalLogin';
import { AppModalRegister } from '../components/AppModalRegister';

export const MainScreen = () => {
    const [modalVisibleLogin, setModalVisibleLogin] = useState(false);
    const [modalVisibleRegister, setModalVisibleRegister] = useState(false);

    return (
        <View style={styles.center}>
            <AppModalLogin
                modalVisible={modalVisibleLogin}
                setModalVisible={setModalVisibleLogin}
            />
            <AppModalRegister
                modalVisible={modalVisibleRegister}
                setModalVisible={setModalVisibleRegister}
            />
            <Image style={styles.image} source={require('../../assets/images/main_logo.png')} />
            <AppTextBold style={{ fontSize: 22, marginTop: 10, color: THEME.MAIN_COLOR }}>
                Играй и выигрывай с нами!
            </AppTextBold>
            <View style={styles.buttons}>
                <AppButton title="Войти" onOpen={() => setModalVisibleLogin(!modalVisibleLogin)} />
                <AppButton
                    title="Зарегистрироваться"
                    onOpen={() => setModalVisibleRegister(!modalVisibleRegister)}
                />
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    center: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        padding: 20,
    },
    image: {
        paddingLeft: 'auto',
        paddingRight: 'auto',
        width: 200,
        height: 200,
    },
    buttons: {
        marginTop: 120,
    },
});
