import React from 'react';
import { createAppContainer } from 'react-navigation';
import { createStackNavigator } from 'react-navigation-stack';
import { Platform } from 'react-native';
import { createBottomTabNavigator } from 'react-navigation-tabs';
import { Ionicons } from '@expo/vector-icons';
import { createMaterialBottomTabNavigator } from 'react-navigation-material-bottom-tabs';

import { MainScreen } from '../screens/MainScreen';
import { HistoryScreen } from '../screens/HistoryScreen';
import { PlayScreen } from '../screens/PlayScreen';
import { ProfileScreen } from '../screens/ProfileScreen';
import { ShopScreen } from '../screens/ShopScreen';
import { TopScreen } from '../screens/TopScreen';
import { THEME } from '../theme';
import { LoginScreen } from '../screens/LoginScreen';
import { RegisterScreen } from '../screens/RegisterScreen';

const navigatorOptions = {
    defaultNavigationOptions: { header: null, textAlign: 'center' },
};

const PlayNavigator = createStackNavigator(
    {
        Play: PlayScreen,
    },
    navigatorOptions,
);

const TopNavigator = createStackNavigator(
    {
        Top: TopScreen,
    },
    navigatorOptions,
);

const ShopNavigator = createStackNavigator(
    {
        Shop: ShopScreen,
    },
    navigatorOptions,
);

const HistoryNavigator = createStackNavigator(
    {
        History: HistoryScreen,
    },
    navigatorOptions,
);

const ProfileNavigator = createStackNavigator(
    {
        Profile: ProfileScreen,
    },
    navigatorOptions,
);

const LoginNavigator = createStackNavigator(
    {
        Login: LoginScreen,
    },
    {
        defaultNavigationOptions: {
            title: 'Логин',
            headerTintColor: THEME.MAIN_COLOR,
            headerStyle: {
                borderBottomColor: THEME.MAIN_COLOR,
                borderBottomWidth: 1,
            },
            headerTitleAlign: Platform.OS === 'android' ? 'left' : 'center',
        },
    },
);

const RegisterNavigator = createStackNavigator(
    {
        Register: RegisterScreen,
    },
    {
        defaultNavigationOptions: {
            title: 'Регистрация',
            headerTintColor: THEME.MAIN_COLOR,
            headerStyle: {
                borderBottomColor: THEME.MAIN_COLOR,
                borderBottomWidth: 1,
            },
            headerTitleAlign: Platform.OS === 'android' ? 'left' : 'center',
        },
    },
);

const buttomTabsConfig = {
    Play: {
        screen: PlayNavigator,
        navigationOptions: {
            tabBarLabel: 'Играть',
            tabBarIcon: (info) => <Ionicons name="play-circle" size={40} color={info.tintColor} />,
        },
    },
    Top: {
        screen: TopNavigator,
        navigationOptions: {
            tabBarLabel: 'Топ',
            tabBarIcon: (info) => <Ionicons name="star" size={40} color={info.tintColor} />,
        },
    },
    Shop: {
        screen: ShopNavigator,
        navigationOptions: {
            tabBarLabel: 'Магазин',
            tabBarIcon: (info) => <Ionicons name="basket-sharp" size={40} color={info.tintColor} />,
        },
    },
    History: {
        screen: HistoryNavigator,
        navigationOptions: {
            tabBarLabel: 'История',
            tabBarIcon: (info) => <Ionicons name="time" size={40} color={info.tintColor} />,
        },
    },
    Profile: {
        screen: ProfileNavigator,
        navigationOptions: {
            tabBarLabel: 'Профиль',
            tabBarIcon: (info) => (
                <Ionicons name="person-circle" size={40} color={info.tintColor} />
            ),
        },
    },
};

const ButtomNavigator = createBottomTabNavigator(buttomTabsConfig, {
    tabBarOptions: {
        keyboardHidesTabBar: true,
        labelStyle: {
            fontSize: 12,
            fontFamily: 'roboto-bold',
        },
        activeTintColor: THEME.MAIN_COLOR,
        style: {
            backgroundColor: '#fff',
            borderTopColor: THEME.MAIN_COLOR,
            borderTopWidth: 1,
            paddingTop: 5,
            paddingBottom: 5,
            height: 70,
        },
    },
});

const authBottomNavigate = {
    Login: {
        screen: LoginNavigator,
        navigationOptions: {
            tabBarLabel: 'Логин',
            tabBarIcon: (info) => <Ionicons name="log-in" size={40} color={info.tintColor} />,
        },
    },
    Register: {
        screen: RegisterNavigator,
        navigationOptions: {
            tabBarLabel: 'Регистрация',
            tabBarIcon: (info) => <Ionicons name="person-add" size={40} color={info.tintColor} />,
        },
    },
};

const AuthNavigatorBottom = createBottomTabNavigator(authBottomNavigate, {
    tabBarOptions: {
        keyboardHidesTabBar: true,
        labelStyle: {
            fontSize: 12,
            fontFamily: 'roboto-bold',
            textAlign: 'center',
        },
        activeTintColor: THEME.MAIN_COLOR,
        style: {
            backgroundColor: '#fff',
            borderTopColor: THEME.MAIN_COLOR,
            borderTopWidth: 1,
            paddingTop: 5,
            paddingBottom: 5,
            height: 70,
        },
    },
});

export const AppNavigator = createAppContainer(ButtomNavigator);
export const AuthNavigator = createAppContainer(AuthNavigatorBottom);
